import {Outlet, Link, useLocation} from 'react-router-dom';
import {useState,useEffect} from 'react';

function App() {
  const [jwtToken, setjwtToken] = useState("");
  const location = useLocation();
  // useEffect(() => {
  //   setjwtToken("rfere");
  // }, [jwtToken])

  return (
  <div className="container">
   <div className="row align-items-center">
    <div className="col">
      <h1 className="mt-3">Go watch a movie</h1>
    </div>
    <div className="col text-end ">
      { jwtToken === ""
      ? location.path!=="/login"?<Link to="/login"><span className="btn btn-primary "> Login </span></Link>:""
      :<span className="btn btn-danger "> Logout </span>
      }
    </div>
    <hr className="mb-3"></hr>
   </div>
   <div className="row">
    <div className="col-md-2">
      <nav>
      <div className="list-group">
        <Link to="/" className="list-group-item list-group-item-action">Home</Link>
        <Link to="/movies" className="list-group-item list-group-item-action">Movies</Link>
        <Link to="/genres" className="list-group-item list-group-item-action">Genres</Link>
        { jwtToken!=="" && <>
        <Link to="/admin/movies/0" className="list-group-item list-group-item-action">Add movie</Link>
        <Link to="/manage-catalogue" className="list-group-item list-group-item-action">Manage movies</Link>
        <Link to="/graphql" className="list-group-item list-group-item-action">GraphQL</Link>
        </>
        } 
      </div>
      </nav>
    </div>
    <div className="col-md-10"> 
   <Outlet context = {{jwtToken, setjwtToken}}/>
    </div>
   </div>

  </div>
  );
}

export default App;
